mapper.select:
SELECT DELIVERY_SEQ, USER_SEQ, DELIVERY_NAME, DELIVERY_PHONE, POSTCODE, ADDRESS, DETADDR, EXTADDR, DELIVERY_NUM
FROM TB_DELIVERY
WHERE DELIVERY_SEQ = #{delivery_seq}

mapper.update:
UPDATE TB_DELIVERY
SET DELIVERY_NUM = #{delivery_num}
WHERE DELIVERY_SEQ = #{delivery_seq}

UPDATE TB_BOOK_RENT
SET RENT_STATUS = 'A'
WHERE RENT_SEQ = #{rent_seq}

UPDATE TB_BOOK_RENT
SET RENT_STATUS = 'B'
WHERE RENT_SEQ = #{rent_seq}
